import React, { Fragment, useState, useEffect, useRef } from "react";
import { Tabs, TabList, TabPanel, Tab } from "react-tabs";
import { User, Unlock } from "react-feather";
import { withRouter, useHistory } from "react-router-dom";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";
import { validateEmail } from "../../utilities/Validations";
import swal from "sweetalert";

//Firebase
import firebase from "../../utilities/firebase";
import {
    getAuth,
    signInWithEmailAndPassword,
    createUserWithEmailAndPassword,
    onAuthStateChanged,
    updateProfile,
} from "firebase/auth";

const LoginTabset = () => {
	const history = useHistory();
	const [formData, setFormData] = useState(defaultValueForm());
	const [formError, setFormError] = useState({});
	const [user, setUser] = useState(false);
	const [idUid, setIdUid] = useState(0);
	const [createId, setCreateId] = useState(false);

	const clickActive = (event) => {
		document.querySelector(".nav-link").classList.remove("show");
		event.target.classList.add("show");
	};

	const onChange = (e) => {
		console.log("VALOR E : ", e)
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

	const registrarse = async () => {
        setFormError({});
        let errors = {};
        let formOk = true;
		console.log("DATOS : ", formData);

        if (!validateEmail(formData.usuario)) {
            swal({
                title: "Registro Usuarios",
                text: "Ingresa un email valido!",
                icon: "warning",
                button: "Aceptar",
            });
            errors.usuario = true;
            formOk = false;
            return;
        }

        if (formData.contraseña.length < 8) {
            swal({
                title: "Registro Usuarios",
                text: "Password debe ser mayor a siete (7) caracteres!",
                icon: "warning",
                button: "Aceptar",
            });
            errors.password = true;
            formOk = false;
            return;
        } 

		if (formData.contraseña != formData.confirmarcontraseña) {
            swal({
                title: "Registro Usuarios",
                text: "Contraseña y confirmar contraseña, son diferentes!",
                icon: "warning",
                button: "Aceptar",
            });
            errors.password = true;
            formOk = false;
            return;
        } 

        console.log("VALOR FORMDATA : ", formData);
	
        //Consulta en la BD de MR para ver si el email esta asociado a una cuenta
        const emailusuario = {
            usuario: formData.usuario,
        };

        //const respuestauser = await ReadUserEmail.getReadUsersEmail(
          //  emailusuario
        //);
        ///console.log("SI USUARIO EXISTE : ", respuestauser);
/*
        if (respuestauser.length > 0) {
            swal({
                title: "Registro Usuarios",
                text: "Por favor revisa el email, ya esta asignado a otra cuenta!",
                icon: "warning",
                button: "Aceptar",
            });
            return;
        }
		*/
        setFormError(errors);

        if (formOk) {
            //setLoading(true);
            //console.log("DATOS USAURIO : ", formData);
            //console.log(formData.password);

            const grabaUsuario = async () => {
                const auth = getAuth(firebase);
                createUserWithEmailAndPassword(
                    auth,
                    formData.usuario,
                    formData.contraseña
                )
                    .then((userCredential) => {
                        // Signed in
                        const user = userCredential.user;
                        //console.log("USER CREDENTIAL : ", user);
                        const auth = getAuth(firebase);

                        onAuthStateChanged(auth, (user) => {
                            if (user) {
                                //alert("ENTRE")

                                const datos = {
                                    uid: user.metadata.createdAt,
                                    medio: "",
                                };

                                setIdUid(user.metadata.createdAt);

                                setUser(true);
                                updateProfile(auth.currentUser, {
                                    displayName: "William Castro",
                                    photoURL: "",
                                })
                                    .then(() => {
                                        swal({
                                            title: "Actualizar Usuarios",
                                            text: "Nombre Usuario Actualizado de forma correcta!",
                                            icon: "success",
                                            button: "Aceptar",
                                        });
                                        createUser(user.metadata.createdAt);
                                        setCreateId(true);
                                    })
                                    .catch((error) => {
                                        swal({
                                            title: "Actualizar Usuarios",
                                            text: "Error Actualizando nombre de Usuario!",
                                            icon: "error",
                                            button: "Aceptar",
                                        });
                                    });
                            } else {
                                setUser(false);
                            }
                        });
                    })
                    .catch((error) => {
						//console.log("ERROR CODE : ",error.code)
						//console.log("ERROR CODE : ",error.message)
                        const errorCode = error.code;
                        const errorMessage = error.message;
                        swal({
                            title: "Registro Usuarios",
                            text: "Error al crear la cuenta!",
                            icon: "error",
                            button: "Aceptar",
                        });
                    });
            };
            grabaUsuario();
        }
        return;
    };

	const createUser = async (iDUser) => {
		console.log("Procedimiento para crear usuario en BD")
	}

	const login = async () => {
        //Consulta en la BD de MR para ver si el email esta asociado a una cuenta

        setFormError({});
        let errors = {};
        let formOk = true;

        if (!validateEmail(formData.usuario)) {
            errors.email = true;
            formOk = false;
        }
        if (formData.password.length < 6) {
            errors.password = true;
            formOk = false;
        }
        setFormError(errors);

        const emailusuario = {
            email: formData.usuario,
        };

        //Consulta en la BD de MR para ver si el email esta asociado a una cuenta
        const respuestauser = await ReadUserEmail.getReadUsersEmail(
            emailusuario
        );

        if (respuestauser) {
            setCodigoToken(respuestauser[0].token);
            if (respuestauser[0].activo === "N") {
                Swal.fire({
                    showCancelButton: false,
                    showConfirmButton: true,
                    html: `<h1>CONTROL DE ACCESO</h1>
                <hr/>
                <br />
                <h2>Ya eres parte de la comunidad MR</h2>
                <hr/>
                <h2>Tienes una cuenta, pero primero la debes activar</h2>
                <hr/>
                `,
                });
            } else {
                if (respuestauser[0].activo === "R") {
                    Swal.fire({
                        showCancelButton: false,
                        showConfirmButton: true,
                        html: `<h1>CONTROL DE ACCESO</h1>
                    <hr/>
                    <br />
                    <h2>Ya eres parte de la comunidad MR</h2>
                    <hr/>
                    <h2>Estamos revisando tus documentos para habilitar tu usuario!</h2>
                    <hr/>
                    `,
                    });
                }
            }
        } else {
            Swal.fire({
                showCancelButton: false,
                showConfirmButton: true,
                html: `<h1>CONTROL DE ACCESO</h1>
            <hr/>
            <br />
            <h2>Aún no eres parte de la comunidad MR</h2>
            <hr/>
            <h2>Que estas esperando crea ya tu cuenta</h2>
            <hr/>
            `,
            });
            //router.push("/my-account");
        }

        if (formOk) {
            setLoading(true);

            const auth = getAuth(firebase);
            signInWithEmailAndPassword(auth, formData.email, formData.password)
                .then((userCredential) => {
                    const user = userCredential.user;
                    //console.log("DATOS USER : ", user);
                    // Lee los datos del usuario para validar si ya esta aActivo

                    const dat = {
                        uid: user.metadata.createdAt,
                    };

                    setIdUid(user.metadata.createdAt);

                    const leeDatosUsuario = async () => {
                        const DatosUsuario = await Users.getUsers(dat);

                        if (DatosUsuario.length > 0) {
                            setUsuario(DatosUsuario);
                            if (DatosUsuario[0].activo === "N") {
                                setShowModal(true);
                                //router.push("/loginaccount");
                            } else {
                                router.push("/");
                            }
                        }
                    };
                    leeDatosUsuario();

                    setLoading(false);
                    //console.log("ACCESO OK");
                    //.push("/");
                })
                .catch((error) => {
                    const errorCode = error.code;
                    const errorMessage = error.message;
                    setLoading(false);
                    swal({
                        title: "CONTROL DE ACCESO",
                        text: "Error al Intentar la Conexion... Intente mas Tarde!",
                        icon: "warning",
                        button: "Aceptar",
                    });
                    //router.push("/");
                });
        }
        //datosusuarios = useSelector((state) => state.userlogged.userlogged);
    };

	return (
		<div>
			<Fragment>
				<Tabs>
					<TabList className="nav nav-tabs tab-coupon">
						<Tab className="nav-link" onClick={(e) => clickActive(e)}>
							<User />
							Ingresa
						</Tab>
						<Tab className="nav-link" onClick={(e) => clickActive(e)}>
							<Unlock />
							Registrarse
						</Tab>
					</TabList>

					<TabPanel>
						<Form className="form-horizontal auth-form">
							<FormGroup>
								<Input
									required=""
									name="usuario"
									type="email"
									className="form-control"
									placeholder="Ingrese usuario"
									onChange={onChange}
									id="Inputusuario"
								/>
							</FormGroup>
							<FormGroup>
								<Input
									required=""
									name="contraseña"
									type="password"
									className="form-control"
									onChange={onChange}
									placeholder="Contraseña"
								/>
							</FormGroup>
							<div className="form-terms">
								<div className="custom-control custom-checkbox mr-sm-2">
									<Input
										type="checkbox"
										className="custom-control-input"
										id="customControlAutosizing"
									/>
									<Label className="d-block">
										<Input
											className="checkbox_animated"
											id="chk-ani2"
											type="checkbox"
										/>
										Recuerdame{" "}
										<span className="pull-right">
											{" "}
											<a href="/#" className="btn btn-default forgot-pass p-0">
												Perdiste tu contraseña
											</a>
										</span>
									</Label>
								</div>
							</div>
							<div className="form-button">
								<Button
									color="primary"
									type="submit"
									onClick={() => routeChange()}
								>
									Ingresar
								</Button>
							</div>
							<div className="form-footer">
								<span>O inicie sesión con plataformas sociales</span>
								<ul className="social">
									<li>
										<a href="/#">
											<i className="icon-facebook"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-twitter-alt"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-instagram"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-pinterest-alt"></i>
										</a>
									</li>
								</ul>
							</div>
						</Form>
					</TabPanel>
					<TabPanel>
						<Form className="form-horizontal auth-form">
							<FormGroup>
								<Input
									required=""
									name="usuario"
									type="email"
									className="form-control"
									placeholder="Ingrese usuario"
									onChange={onChange}
									id="idusuario"
								/>
							</FormGroup>
							<FormGroup>
								<Input
									required=""
									name="contraseña"
									type="password"
									className="form-control"
									placeholder="Contraseña"
									onChange={onChange}	
								/>
							</FormGroup>
							<FormGroup>
								<Input
									required=""
									name="confirmarcontraseña"
									type="password"
									className="form-control"
									placeholder="Confirmar Contraseña"
									onChange={onChange}
								/>
							</FormGroup>
							<div className="form-terms">
								<div className="custom-control custom-checkbox mr-sm-2">
									<Input
										type="checkbox"
										className="custom-control-input"
										id="customControlAutosizing"
									/>
									<Label className="d-block">
										<Input
											className="checkbox_animated"
											id="chk-ani2"
											type="checkbox"
										/>
										Acepto términos y condiciones{" "}
										<span>
											<a href="/#">Terms &amp; Condiciones</a>
										</span>
									</Label>
								</div>
							</div>
							<div className="form-button">
								<Button
									color="primary"
									onClick={() => registrarse()}
								>
									Registrarse
								</Button>
							</div>
							<div className="form-footer">
								<span>O inicie sesión con plataformas sociales</span>
								<ul className="social">
									<li>
										<a href="/#">
											<i className="icon-facebook"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-twitter-alt"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-instagram"></i>
										</a>
									</li>
									<li>
										<a href="/#">
											<i className="icon-pinterest-alt"></i>
										</a>
									</li>
								</ul>
							</div>
						</Form>
					</TabPanel>
				</Tabs>
			</Fragment>
		</div>
	);
};

function defaultValueForm() {
    return {
        usuario: "",
        contraseña: "",
        confirmarcontraseña: ""
    };
}

export default withRouter(LoginTabset);
