import { all, put, takeEvery } from 'redux-saga/effects';
import { polyfill } from 'es6-promise';
import {
    actionTypes,
    getPokemonsSuccess
} from './action';

polyfill();

function* getPokemonsSaga({ payload }) {
    try {
        yield put(getPokemonsSuccess(payload));
    } catch (err) {
        console.log(err);
    }
}

export default function* rootSaga() {
    yield all([takeEvery(actionTypes.GET_POKEMONS, getPokemonsSaga)]);
}
