				};
		*/
		console.log("DATOS FORMDATA : ", formdata);
		fetch("https://sitbusiness.co/cyclewear/api/709", {
			method: "POST",
			body: formdata,
		}).then((response) => {
			if (response) {
				console.log("RESPONSE : ", response)

