															e.target
																.value
														)
													}
												>
													<option
														selected
														className="select-fontsize ps-form__label">
														Seleccione Ancho
													</option>
													{listRosca && listRosca.map(
														(
															itemselect
														) => {
															return (
																<option
																	value={
																		itemselect.value
																	}>
																	{
																		itemselect.label
																	}
																</option>
															);
														}
													)}
												</select>
											</div>
										</FormGroup>
									)
									:
									null
							}

							{
								muestraMaterial ?
									(
										<FormGroup className="form-group mb-3 row">

											<Label className="col-xl-2 col-sm-4 mb-0">
												Material:
											</Label>
											<div className="col-xl-4 col-sm-7">
												<select
													//disabled="disabled"
													className="form-control digits"
													onClick={(e) =>
														handleChangeMaterial(
															e.target
																.value
														)
													}
												>
													<option
														selected
														className="select-fontsize ps-form__label">
														Seleccione material
													</option>
													{listMaterial && listMaterial.map(
														(
															itemselect
														) => {
															return (
																<option
																	value={
																		itemselect.value
																	}>
																	{
																		itemselect.label
																	}
																</option>
															);
														}
													)}
												</select>
											</div>
										</FormGroup>
									)
									:
									null
							}

							{
								muestraBrazoBiela ?
									(
										<FormGroup className="form-group mb-3 row">
											<Label className="col-xl-2 col-sm-4 mb-0">
												Brazo Biela:
											</Label>
											<div className="col-xl-4 col-sm-7">
												<select
													//disabled="disabled"
													className="form-control digits"
													onClick={(e) =>
														handleChangeBrazoBiela(
															e.target
																.value
														)
													}
												>
													<option
														selected
														className="select-fontsize ps-form__label">
														Seleccione Rosca
													</option>
													{listBrazoBiela && listBrazoBiela.map(
														(
															itemselect
														) => {
															return (
																<option
																	value={
																		itemselect.value
																	}>
																	{
																		itemselect.label
																	}
																</option>
															);
														}
													)}
												</select>
											</div>
										</FormGroup>
									)
									:
									null
							}
							<hr />
							<div className="form form-label-center">
								<FormGroup className="form-group mb-1 row">
									<Label className="col-xl-2 col-sm-3 mb-0">
										Precio base:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<NumberFormat
											className="form-control"
											name="preciobasevariante"
											//placeholder="Ingrese precio base"
											thousandSeparator={true}
											prefix={"$"}
										/>
									</div>
									<Label className="col-xl-2 col-sm-3 mb-0">
										Precio venta:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<NumberFormat
											className="form-control"
											name="precioventavariante"
											//placeholder="Ingrese precio del producto"
											thousandSeparator={true}
											prefix={"$"}
										/>
									</div>
									<Label className="col-xl-2 col-sm-3 mb-0">
										Cantidad:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<Input
											className="form-control"
											name="cantidadvariante"
											type="text"
										/>
									</div>
								</FormGroup>

								<FormGroup className="form-group mb-0 row">
									<Label className="col-xl-2 col-sm-3 mb-0">
										CÃ³digo de barras:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<Input
											className="form-control"
											name="codigobarravariante"
											type="text"
										/>
									</div>
									<Label className="col-xl-2 col-sm-3 mb-0">
										SKU:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<Input
											className="form-control"
											name="skuvariante"
											type="text"
										/>
									</div>
									<Label className="col-xl-2 col-sm-3 mb-0">
										Tax code:
									</Label>
									<div className="col-xl-2 col-sm-7">
										<Input
											className="form-control"
											name="taxcodevariante"
											type="text"
										/>
									</div>
								</FormGroup>
							</div>
						</Form>
					</ModalBody>
					<ModalFooter>
						<Button color="success" onClick={grabarVariantes}>Grabar variante</Button>
						<Button color="primary" onClick={grabarVariantes}>Grabar variante</Button>
					</ModalFooter>
				</Modal>

			</Container>
		</Fragment >
	);
};

function defaultValueForm() {
	return {
		preciobasevariante: "",
		precioventavariante: "",
		cantidadvariante: "",
		codigobarravariante: "",
		skuvariante: "",
		taxcodevariante: ""
	};
}

export default Add_product;
