            setLoading(true);
            const newDetPed = [];

            let control = 0;
            let numeropedidos = lisPedidos.length;

            lisPedidos && lisPedidos.map((facturas, index) => {
                const leer = async () => {
                    const params = {
                        factura: facturas.id_fact
                    }
                    //console.log("LISTADO PEDIDOS : ", params)

                    await axios({
                        method: 'get',
                        url: 'https://sitbusiness.co/cyclewear/api/202', params
