import React, { Fragment, useState, useEffect } from "react";
import Breadcrumb from "../../common/breadcrumb";
import "react-toastify/dist/ReactToastify.css";
import { data } from "../../../assets/data/product-types";
import Datatable from "../../common/datatable";
import MaterialTable from "material-table";
import {
	Button,
	Card,
	CardBody,
	CardHeader,
	Col,
	Container,
	Form,
	FormGroup,
	Input,
	Label,
	Modal,
	ModalBody,
	ModalFooter,
	ModalHeader,
	Row,
} from "reactstrap";

const ProductType = () => {
	const [open, setOpen] = useState(false);
	const [listTiposProductos, setListTiposProductos] = useState([]);

	const onOpenModal = () => {
		setOpen(true);
	};

	const onCloseModal = () => {
		setOpen(false);
	};

	useEffect(() => {
		let data = JSON.parse(localStorage.getItem('datosentorno'));
		//console.log("DATA PROVEEDORES: ", JSON.parse(data))
		//console.log("DATA UNO : ", data.vgl_condicionproducto);
		setListTiposProductos(data.vgl_tiposproductos);
		console.log("TIPOS DE PRODUCTOS : ", data.vgl_tiposproductos)
	}, []);

	const columnas = [
		{
			field: 'id',
			title: 'ID Tipo',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombretipoproducto',
			title: 'Nombre tipo producto',
			cellStyle: { minWidth: 150 }
		},
		{
			field: 'descripcion',
			title: 'Descripción',
			cellStyle: { minWidth: 300 }
		},
		{
			field: 'estado',
			title: 'Estado',
			cellStyle: { minWidth: 50 }
		}
	]


	return (
		<Fragment>
			<Breadcrumb title="Tipos de Productos" parent="Physical" />
			{/* <!-- Container-fluid starts--> */}
			<Container fluid={true}>
				<Row>
					<Col sm="12">
						<Card>
							<CardHeader>
								<h5>Tipos de Productos</h5>
							</CardHeader>
							<CardBody>
								<div className="btn-popup pull-right">
									<Button
										type="button"
										color="primary"
										onClick={onOpenModal}
										data-toggle="modal"
										data-original-title="test"
										data-target="#exampleModal"
									>
										Crear Tipos de Productos
									</Button>
									<Modal isOpen={open} toggle={onCloseModal}>
										<ModalHeader toggle={onCloseModal}>
											<h5
												className="modal-title f-w-600"
												id="exampleModalLabel2"
											>
												Add Physical Product
											</h5>
										</ModalHeader>
										<ModalBody>
											<Form>
												<FormGroup>
													<Label
														htmlFor="recipient-name"
														className="col-form-label"
													>
														Category Name :
													</Label>
													<Input type="text" className="form-control" />
												</FormGroup>
												<FormGroup>
													<Label
														htmlFor="message-text"
														className="col-form-label"
													>
														Category Image :
													</Label>
													<Input
														className="form-control"
														id="validationCustom02"
														type="file"
													/>
												</FormGroup>
											</Form>
										</ModalBody>
										<ModalFooter>
											<Button
												type="button"
												color="primary"
												onClick={() => onCloseModal("VaryingMdo")}
											>
												Save
											</Button>
											<Button
												type="button"
												color="secondary"
												onClick={() => onCloseModal("VaryingMdo")}
											>
												Close
											</Button>
										</ModalFooter>
									</Modal>
								</div>
								<div className="clearfix"></div>
								<div id="basicScenario" className="product-physical">
									<MaterialTable
										columns={columnas}
										data={listarOrdenes}
										fontSize={24}
										title="TIPOS DE PRODUCTOS"
										actions={[
											{
												icon: 'edit',
												tooltip: 'Editar Orden',
												onClick: (event, rowData) => seleccionarOrden(rowData, "Editar")
											},
											{
												icon: CancelIcon,
												tooltip: 'Cancelar Orden',
												onClick: (event, rowData) => ConfirmeCancelarOrden(rowData, "Cancelar")
											},
											{
												icon: AccessAlarmIcon,
												tooltip: 'Pendiente Orden',
												onClick: (event, rowData) => crearPendientes(rowData, "Pendiente")
											}
											/*
											 {
											   icon: BuildIcon,
											   tooltip: 'Asignar Herramientas',
											   onClick: (event, rowData) => asignarHerramientas(rowData, "Herramientas")
											 },
											 */
										]}
										options={{
											actionsColumnIndex: 11
										}}
										localization={{
											header: {
												actions: "Acciones"
											}
										}}
										detailPanel={[
											{
												tooltip: 'Estados del Equipo',
												render: rowData => {
													return (
														<div
															style={{
																fontSize: 18,
																textAlign: 'center',
																color: 'white',
																backgroundColor: '#0277bd',
															}}
														>
															<Button variant="contained">Datos Contacto: {rowData.primer_nombre_con} { } {rowData.primer_apellido_con} { }
																Telefono: {rowData.telefono_con} { } email: {rowData.email_con}{ } Proveedor : {rowData.razonsocial_int}
															</Button>

														</div>
													)
												},
											},
										]}
									/>
								</div>
							</CardBody>
						</Card>
					</Col>
				</Row>
			</Container>
			{/* <!-- Container-fluid Ends--> */}
		</Fragment>
	);
};

export default ProductType;
