import React, { Fragment, useState, useEffect } from "react";
import Breadcrumb from "../../common/breadcrumb";
import "react-toastify/dist/ReactToastify.css";
import MaterialTable from "material-table";
import CancelIcon from '@material-ui/icons/Cancel';
import EditIcon from '@material-ui/icons/Edit';
import {
	Button,
	Card,
	CardBody,
	CardHeader,
	Col,
	Container,
	Form,
	FormGroup,
	Input,
	Label,
	Modal,
	ModalBody,
	ModalFooter,
	ModalHeader,
	Row,
} from "reactstrap";

const Productvariables = () => {
	const [open, setOpen] = useState(false);
	const [listVariablesProductos, setListVariablesProductos] = useState([]);

	const onOpenModal = () => {
		setOpen(true);
	};

	const onCloseModal = () => {
		setOpen(false);
	};

	useEffect(() => {
		let data = JSON.parse(localStorage.getItem('datosentorno'));
		//console.log("DATA PROVEEDORES: ", JSON.parse(data))
		//console.log("DATA UNO : ", data.vgl_condicionproducto);
		setListVariablesProductos(data.vgl_variablesproducto);
		console.log("VARIABLES PRODUCTOS : ", data.vgl_variablesproducto)
	}, []);

	const seleccionarOrden = (orden, caso) => {
	
	}
/*
,
            headerStyle: {
                backgroundColor: '#0277bd'
            }
*/
	const columnas = [
		{
			field: 'nombretipoproducto',
			title: 'Tipo Producto',
			cellStyle: { minWidth: 50 },
			cellStyle: { maxWidth: 50 }
		},
		{
			field: 'nombrecategoriauno',
			title: 'Categoría',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriados',
			title: 'SubCategoría',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriatres',
			title: 'SubCategoría Tres',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriauno',
			title: 'VariableUno',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriauno',
			title: 'VairiableDos',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriauno',
			title: 'VariableTres',
			cellStyle: { minWidth: 50 }
		},
		{
			field: 'nombrecategoriauno',
			title: 'VariableCuatro',
			cellStyle: { minWidth: 50 }
		}
	]

	return (
		<Fragment>
			<Breadcrumb title="Variables por Productos" parent="Physical" />
			{/* <!-- Container-fluid starts--> */}
			<Container fluid={true}>
				<Row>
					<Col sm="12">
						<Card>
							<CardHeader>
								<h5>Variables por Productos</h5>
							</CardHeader>
							<CardBody>
								<div className="btn-popup pull-right">
									<Button
										type="button"
										color="primary"
										onClick={onOpenModal}
										data-toggle="modal"
										data-original-title="test"
										data-target="#exampleModal"
									>
										Variables por Productos
									</Button>
									<Modal isOpen={open} toggle={onCloseModal}>
										<ModalHeader toggle={onCloseModal}>
											<h5
												className="modal-title f-w-600"
												id="exampleModalLabel2"
											>
												Agregar Producto
											</h5>
										</ModalHeader>
										<ModalBody>
											<Form>
												<FormGroup>
													<Label
														htmlFor="recipient-name"
														className="col-form-label"
													>
														Nombre Tipo Producto :
													</Label>
													<Input type="text" className="form-control" />
												</FormGroup>
												<FormGroup>
												<Label
														htmlFor="recipient-name"
														className="col-form-label"
													>
														Descripción del Tipo Producto :
													</Label>
													<Input type="text" className="form-control" />
												
												</FormGroup>
											</Form>
										</ModalBody>
										<ModalFooter>
											<Button
												type="button"
												color="primary"
												onClick={() => onCloseModal("VaryingMdo")}
											>
												Grabar
											</Button>
											<Button
												type="button"
												color="secondary"
												onClick={() => onCloseModal("VaryingMdo")}
											>
												Cerrar
											</Button>
										</ModalFooter>
									</Modal>
								</div>
								<div className="clearfix"></div>
								<div id="basicScenario" className="product-physical">
									<MaterialTable
										columns={columnas}
										data={listVariablesProductos}
										fontSize={16}
										title="VARIABLES POR PRODUCTO"
										actions={[	
											{
												icon: EditIcon,
												tooltip: 'Editar Variables',
												onClick: (event, rowData) => seleccionarOrden(rowData, "Editar")
											},
											{
												icon: CancelIcon,
												tooltip: 'Inactivar Tipo Producto',
												onClick: (event, rowData) => seleccionarOrden(rowData, "Cancelar")
											}
										]}
										options={{
											actionsColumnIndex: 11,
											headerStyle: { backgroundColor: '#015CAB', fontSize: 14, color: 'white' },
											rowStyle: {
												fontSize: 14,
											}
										}}
										localization={{
											header: {
												actions: "Acciones"
											}
										}}
									/>
								</div>
							</CardBody>
						</Card>
					</Col>
				</Row>
			</Container>
			{/* <!-- Container-fluid Ends--> */}
		</Fragment>
	);
};

export default Productvariables;
