												<FormGroup className="form-group mb-3 row">
													<Label className="col-xl-2 col-sm-4 mb-0">
														Tamaño Accesorio:
													</Label>
													<div className="col-xl-4 col-sm-7">
														<select
															//disabled="disabled"
															className="form-control digits"
															onClick={(e) =>
																handleChangeTamanoAccesorio(
																	e.target
																		.value
																)
															}
														>
															<option
																selected
																className="select-fontsize ps-form__label">
																Seleccione tamaño accesorio
															</option>
															{listTamanoAccesorios && listTamanoAccesorios.map(
																(
																	itemselect
																) => {
																	return (
																		<option
																			value={
																				itemselect.value
																			}>
																			{
																				itemselect.label
																			}
																		</option>
																	);
																}
															)}
														</select>
													</div>
													<Label className="col-xl-2 col-sm-4 mb-0">
														Tamano Componente:
													</Label>
													<div className="col-xl-4 col-sm-7">
														<select
															//disabled="disabled"
															className="form-control digits"
															onClick={(e) =>
																handleChangeTamanoComponente(
																	e.target
																		.value
																)
															}
														>
															<option
																selected
																className="select-fontsize ps-form__label">
																Seleccione tamano componente
															</option>
															{listTamanoComponentes && listTamanoComponentes.map(
																(
																	itemselect
																) => {
																	return (
																		<option
																			value={
																				itemselect.value
																			}>
																			{
																				itemselect.label
																			}
																		</option>
																	);
																}
															)}
														</select>
													</div>
												</FormGroup>
												<FormGroup className="form-group mb-3 row">
													<Label className="col-xl-2 col-sm-4 mb-0">
														Llantas y Neumáticos:
													</Label>
													<div className="col-xl-4 col-sm-7">
														<select
															//disabled="disabled"
															className="form-control digits"
															onClick={(e) =>
																handleChangeLlantasyNeumaticos(
																	e.target
																		.value
																)
															}
														>
															<option
																selected
																className="select-fontsize ps-form__label">
																Seleccione Llanta y Neumático
															</option>
															{listLlantasyNeumaticos && listLlantasyNeumaticos.map(
																(
																	itemselect
																) => {
																	return (
																		<option
																			value={
																				itemselect.value
																			}>
																			{
																				itemselect.label
																			}
																		</option>
																	);
																}
															)}
														</select>
													</div>
													<Label className="col-xl-2 col-sm-4 mb-0">
														Ruedas y Partes:
													</Label>
													<div className="col-xl-4 col-sm-7">
														<select
															//disabled="disabled"
															className="form-control digits"
															onClick={(e) =>
																handleChangeRuedasyPartes(
																	e.target
																		.value
																)
															}
														>
															<option
																selected
																className="select-fontsize ps-form__label">
																Seleccione ruedas y partes
															</option>
															{listRuedasyPartes && listRuedasyPartes.map(
																(
																	itemselect
																) => {
																	return (
																		<option
																			value={
																				itemselect.value
																			}>
																			{
																				itemselect.label
																			}
																		</option>
																	);
																}
															)}
														</select>
													</div>
												</FormGroup>


												<FormGroup className="form-group mb-3 row">
													<Label className="col-xl-2 col-sm-4 mb-0">
														Product Name :
													</Label>
													<div className="col-xl-8 col-sm-7">
														<Input
															className="form-control"
															name="product_name"
															id="validationCustom01"
															type="text"
															required
														/>
													</div>
													<div className="valid-feedback">Looks good!</div>
												</FormGroup>
												<FormGroup className="form-group mb-3 row">
													<Label className="col-xl-3 col-sm-4 mb-0">
														Price :
													</Label>
													<div className="col-xl-8 col-sm-7">
														<Input
															className="form-control mb-0"
															name="price"
															id="validationCustom02"
															type="number"
															required
														/>
													</div>
													<div className="valid-feedback">Looks good!</div>
												</FormGroup>
												<FormGroup className="form-group mb-3 row">
													<Label className="col-xl-3 col-sm-4 mb-0">
														Product Code :
													</Label>
													<div className="col-xl-8 col-sm-7">
														<Input
															className="form-control "
															name="product_code"
															id="validationCustomUsername"
															type="number"
															required
														/>
													</div>
													<div className="invalid-feedback offset-sm-4 offset-xl-3">
														Please choose Valid Code.
													</div>
												</FormGroup>
											</div>
											<Form>
												<FormGroup className="form-group row">
													<Label className="col-xl-3 col-sm-4 mb-0">
														Select Size :
													</Label>
													<div className="col-xl-8 col-sm-7">
														<select
															className="form-control digits"
															id="exampleFormControlSelect1"
														>
															<option>Small</option>
															<option>Medium</option>
															<option>Large</option>
															<option>Extra Large</option>
														</select>
													</div>
												</FormGroup>
												<FormGroup className="form-group row">
													<Label className="col-xl-3 col-sm-4 mb-0">
														Total Products :
													</Label>
													<fieldset className="qty-box ml-0">
														<div className="input-group bootstrap-touchspin">
															<div className="input-group-prepend">
																<Button
																	className="btn btn-primary btn-square bootstrap-touchspin-down"
																	type="button"
																	onClick={DecreaseItem}
																>
																	<i className="fa fa-minus"></i>
																</Button>
															</div>
															<div className="input-group-prepend">
																<span className="input-group-text bootstrap-touchspin-prefix"></span>
															</div>
															<Input
																className="touchspin form-control"
																type="text"
																value={quantity}
																onChange={handleChange}
															/>
															<div className="input-group-append">
																<span className="input-group-text bootstrap-touchspin-postfix"></span>
															</div>
															<div className="input-group-append ml-0">
																<Button
																	className="btn btn-primary btn-square bootstrap-touchspin-up"
																	type="button"
																	onClick={IncrementItem}
																>
																	<i className="fa fa-plus"></i>
																</Button>
															</div>
														</div>
													</fieldset>
												</FormGroup>
												<FormGroup className="form-group row">
													<Label className="col-xl-3 col-sm-4">
														Add Description :
													</Label>
													<div className="col-xl-8 col-sm-7 description-sm">
														<CKEditors
															activeclassName="p10"
															events={{
																blur: onBlur,
																afterPaste: afterPaste,
																change: onChange,
															}}
														/>
													</div>
												</FormGroup>
											</Form>
											<div className="offset-xl-3 offset-sm-4">
												<Button type="submit" color="primary">
													Grabar producto
												</Button>
												<Button type="button" color="light">
													Discard
												</Button>
											</div>
										</Form>
									</Col>
									<Col xl="5">
										<div className="add-product">
											<Row>
												<Col xl="9 xl-50" sm="6 col-9">
													<img
														src={one}
														alt=""
														className="img-fluid tamañofotounocrearproducto image_zoom_1 blur-up lazyloaded"
													/>
												</Col>
											</Row>
											<br />

											<ul className="file-upload-produc" />


											<div>
												{dummyimgs.map((res, i) => {
													return (
														<li key={i}>
															<div className="box-input-file">
																<Input
																	className="upload"
																	type="file"
																	onChange={(e) => _handleImgChange(e, i)}
																/>
																<img
																	alt=""
																	src={res.img}
																	style={{ width: 50, height: 50 }}
																/>
															</div>
														</li>
													);
												})}
											</div>


										</div>
									</Col>
								</Row>
							</CardBody>
						</Card>
					</Col>
				</Row>
			</Container>
		</Fragment>
	);
};

export default Add_product;
